import api from './apiService'

const endpoints = {
    register: '/register',
    login: '/login',
    me: '/me',
    logout: '/logout'

}

export const registerUser = async (registerData) => {
    const {data} = await api.post(endpoints.register,registerData)
    return data
};
export const loginUser = async (loginData) =>  {
    const  {data}  = await api.post(endpoints.login,loginData)

    if(data) localStorage.setItem("user", data)
};

export const getUser = () => {
    let userData = localStorage.getItem("user");
    let user = null;
    if(localStorage.getItem("token")) {
        user = userData
    }

    return user;
}

export const logoutUser = async () => await api.post(endpoints.logout);
