import axios from "axios";

const api = axios.create({
    baseURL: "http://localhost:8000/",

})

api.interceptors.request.use(function (req) {
  console.log(req);
    const token = localStorage.getItem("token");

    if (token) {
      req.headers["Authorization"] = `Bearer ${token}`;
